import React, { useLayoutEffect, useState } from "react";
import BodyContainer from "../components/BodyContainer";
import FlexBox from "../components/FlexBox";
import { videosGet } from "../util/api.js";

const MainPage = () => {
    const [videosData, setVideosData] = useState([]);
    const [mainVideoArr, setMainVideoArr] = useState([]);

    useLayoutEffect(() => {
        videosGet()
            .then((res) => {
                setVideosData(res);
            })
            .catch((error) => {
                console.error("videosGet error:", error);
            });
    }, []);

    useLayoutEffect(() => {
        const slicedVideosData = [...videosData].slice(0, 3);
        setMainVideoArr(slicedVideosData);
    }, [videosData]);

    console.log("^^ttt", videosData);

    return (
        <BodyContainer className="pt-[60px] md:pt-[100px]">
            <div className="mt-[40px] flex flex-col md:flex-row items-center">
                <span className="font-medium font-16 md:font-22 color-green-5e mr-[10px]">
                    Daily TIP.
                </span>
                <span className="font-medium md:font-16">
                    다가오는 장마철 천연 정화석을 구비하여 습기를 제거해보세요
                </span>
            </div>
            <div className="h-[500px] pt-[20px]">TODO게시판들 자리</div>
            <FlexBox justify="space-between" align="center" className="pb-[12px]">
                <div className="md:font-22 color-green-5e font-medium">자취 정보 영상</div>
                <button className="font-medium">더 많은 영상보기</button>
            </FlexBox>
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4 pb-[80px]">
                {videosData.map((item, index) => (
                    <FlexBox key={index}>
                        <button>{item.title}</button>
                        {footerMenu.length - 1 !== index && (
                            <div className="mx-[12px] h-[14px] w-[1px] bg-[#c7c9cd]" />
                        )}
                    </FlexBox>
                ))}

                <div className="iframeContainer iframe16To9">
                    <iframe
                        title={`main_video_${index}`}
                        src="https://www.youtube.com/embed/tgbNymZ7vqY"
                    ></iframe>
                </div>
                <div className="iframeContainer iframe16To9">
                    <iframe src="https://www.youtube.com/embed/tgbNymZ7vqY"></iframe>
                </div>
                <div className="iframeContainer iframe16To9">
                    <iframe src="https://www.youtube.com/embed/tgbNymZ7vqY"></iframe>
                </div>
            </div>
        </BodyContainer>
    );
};

export default MainPage;
