import React, { useState } from "react";
import styled from "styled-components";
import BodyContainer from "../components/BodyContainer";
import { joinPost } from "../util/api";

const SubText = styled.div`
    color: #808080;
    font-size: 2.2rem;
`;

const CheckboxContainer = styled.div`
    display: flex;
    align-items: center;
`;

const CheckboxInput = styled.input`
    margin-right: 8px;
`;

const CheckboxLabel = styled.label`
    font-size: 1.4rem;
`;

const Join = () => {
    const [termsUseState, setTermsUseState] = useState(false);
    const [userData, setUserData] = useState({
        isTermOfUseCheck: false,
        isPrivacyCheck: false,
        username: "",
        password: "",
        nickname: "",
        email: "",
        number: "",
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!privacyCheck(userData.password)) {
            return;
        }

        joinPost(userData)
            .then((response) => {
                console.log("joinPost response:", response);
            })
            .catch((error) => {
                console.error("joinPost error:", error);
            });
    };

    const privacyCheck = (value) => {
        const regex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
        console.log("^^regex.test(value)", regex.test(value));
        return regex.test(value);
    };

    const termsUseView = () => {
        return (
            <div className="flex flex-col h-[100vh]">
                <div className="color-green-5e py-[20px] border-b font-medium font-28">
                    인디팬더 회원가입
                </div>
                <div className="py-[20px]">
                    <SubText>이용약관</SubText>
                    <CheckboxContainer>
                        <CheckboxInput
                            type="checkbox"
                            checked={false}
                            // onChange={() => onChange(!isChecked)}
                        />
                        <CheckboxLabel>{"gdjlsdakj;fsdjljdsaflk"}</CheckboxLabel>
                    </CheckboxContainer>
                    <div>이용약관 네모</div>
                    <div>체크박스</div>
                </div>
            </div>
        );
    };

    return (
        <BodyContainer>
            {!termsUseState ? (
                termsUseView()
            ) : (
                <form onSubmit={handleSubmit}>
                    {Object.keys(userData).map((key, index) => {
                        if (key === "isTermOfUseCheck" || key === "isPrivacyCheck") {
                            return null;
                        }
                        return (
                            <input
                                key={index}
                                type={key === "password" ? "password" : "text"}
                                placeholder={key.charAt(0).toUpperCase() + key.slice(1)}
                                value={userData[key]}
                                onChange={(e) =>
                                    setUserData((prev) => ({
                                        ...prev,
                                        [key]: e.target.value,
                                    }))
                                }
                            />
                        );
                    })}
                    <button type="submit">Register</button>
                </form>
            )}
        </BodyContainer>
    );
};

export default Join;
