import React, { useState } from "react";
import { ButtonGroup, ToggleButton } from "react-bootstrap";
import LogoImg from "../resource/images/header_logo.png";
import BodyContainer from "./BodyContainer";
import FlexBox from "./FlexBox";

const aa = {
    color: "red",
};

const Header = () => {
    const [tabCurrent, setTabCurrent] = useState(0);
    const headerMenu = [
        {
            index: 0,
            title: "메인",
            link: "/",
        },
        {
            index: 1,
            title: "게시판",
            link: "/board",
        },
        {
            index: 2,
            title: "자취생활",
            link: "/life",
        },
    ];

    return (
        <BodyContainer className="border-b-[1px] bg-white fixed top-0 left-0 right-0">
            <FlexBox justify="space-between" className="h-[60px] md:h-[100px]">
                <FlexBox>
                    <img src={LogoImg} alt="" className="h-[40px] md:h-[70px] md:pr-8" />
                    <ButtonGroup className="h-full bg-black">
                        {headerMenu.map((item, index) => (
                            <ToggleButton
                                key={index}
                                id={`radio-${index}`}
                                type="radio"
                                variant="secondary"
                                name="radio"
                                value={item.title}
                                checked={item.index === tabCurrent}
                                onChange={() => setTabCurrent(index)}
                            >
                                {item.title}
                            </ToggleButton>
                        ))}
                    </ButtonGroup>
                </FlexBox>
                <div>NICK1</div>
            </FlexBox>
        </BodyContainer>
    );
};

export default Header;
