import React, { useState } from "react";
import { Nav } from "react-bootstrap";
import { FaRegUser } from "react-icons/fa";
import { IoIosSearch } from "react-icons/io";
import { useHistory } from "react-router-dom";
import LogoImg from "../resource/images/header_logo.png";
import BodyContainer from "./BodyContainer";
import FlexBox from "./FlexBox";
import Icon from "./Icon";

const Header = () => {
    const history = useHistory();

    const handleClick = () => {
        // 버튼 클릭 시 "/" 경로로 이동
        history.push("/");
    };

    const [tabCurrent, setTabCurrent] = useState(0);
    const headerMenu = [
        {
            index: 0,
            title: "메인",
        },
        {
            index: 1,
            title: "게시판",
        },
        {
            index: 2,
            title: "자취생활",
        },
    ];

    return (
        <BodyContainer className="border-b-[1px] bg-white">
            <FlexBox justify="space-between" className="h-[60px] md:h-[100px]">
                <FlexBox className="h-full">
                    <img
                        src={LogoImg}
                        alt=""
                        className="h-[40px] md:h-[70px] md:mr-8 cursor-pointer"
                    />
                    <Nav className="h-full flex hidden lg:inline-flex">
                        {headerMenu.map((item, index) => (
                            <Nav.Item key={index} className="h-full">
                                <FlexBox className="h-full font-medium">
                                    <Nav.Link
                                        onClick={() => {
                                            if (index !== tabCurrent) {
                                                setTabCurrent(index);
                                            }
                                        }}
                                        className="h-full py-[0] px-[25px] flex items-center"
                                        style={{
                                            color: tabCurrent === index ? "#5e913b" : "black",
                                            borderBottom:
                                                tabCurrent === index
                                                    ? "2px solid #5e913b"
                                                    : "2px solid  white",
                                            transition: "color 0.3s, border-bottom 0.3s",
                                        }}
                                    >
                                        {item.title}
                                    </Nav.Link>
                                </FlexBox>
                            </Nav.Item>
                        ))}
                    </Nav>
                </FlexBox>
                <button onClick={handleClick}>Go to Home</button>
                <FlexBox>
                    <FlexBox className="hidden lg:inline-flex md:mr-[20px]">
                        검색
                        <Icon icon={IoIosSearch} size={20} />
                    </FlexBox>
                    <button className="user-button select-none flex items-center px-[7px] py-[4px]">
                        <Icon icon={FaRegUser} size={15} />
                        <div className="ml-1">ouogani</div>
                    </button>
                </FlexBox>
            </FlexBox>
        </BodyContainer>
    );
};

export default Header;
