import React, { useState } from "react";
import { Nav } from "react-bootstrap/";
import LogoImg from "../resource/images/header_logo.png";
import BodyContainer from "./BodyContainer";
import FlexBox from "./FlexBox";

const Header = () => {
    const [tabCurrent, setTabCurrent] = useState(0);
    const headerMenu = [
        {
            index: 0,
            title: "메인",
        },
        {
            index: 1,
            title: "게시판",
        },
        {
            index: 2,
            title: "자취생활",
        },
    ];

    return (
        <BodyContainer className="border-b-[1px] bg-white">
            <FlexBox justify="space-between" className="h-[60px] md:h-[100px]">
                <FlexBox className="h-full">
                    <img
                        src={LogoImg}
                        alt=""
                        className="h-[40px] md:h-[70px] md:pr-8"
                        onClick={""}
                    />
                    <Nav className="h-full flex hidden md:inline-flex">
                        {headerMenu.map((item, index) => (
                            <Nav.Item key={index} className="h-full">
                                <FlexBox className="h-full">
                                    <Nav.Link
                                        onClick={() => {
                                            setTabCurrent(index);
                                        }}
                                        className="h-full py-[0] px-[25px] flex items-center"
                                        style={{
                                            color: tabCurrent === index ? "#5e913b" : "black",
                                            borderBottom:
                                                tabCurrent === index
                                                    ? "2px solid #5e913b"
                                                    : "2px solid  white",
                                            transition: "color 0.3s, border-bottom 0.3s",
                                        }}
                                    >
                                        {item.title}
                                    </Nav.Link>
                                </FlexBox>
                            </Nav.Item>
                        ))}
                    </Nav>

                    {/* eventKey="disabled" */}
                </FlexBox>
                <div>qdqd</div>
            </FlexBox>
        </BodyContainer>
    );
};

export default Header;
