import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import BodyContainer from "../components/BodyContainer";
import { constant } from "../constants/constant";
import { joinPost, nickUniquePost } from "../util/api";
import FlexBox from "./FlexBox";

const SubText = styled.div`
    color: #808080;
    font-size: 2.2rem;
`;
const ScrollableBox = styled.textarea.attrs({ readOnly: true })`
    height: 160px;
    width: 100%;
    overflow-y: auto;
    border: 1px solid #ccc;
    padding: 10px 16px;
    margin: 12px 0;
    border-radius: 5px;
    font-size: 1.6rem;
    resize: none;

    &:focus {
        outline: none;
        border-color: #5e913b;
    }
`;
const StyledCheckbox = styled.input`
    appearance: none;
    width: 1.6rem;
    height: 1.6rem;
    border: 1px solid #808080;
    border-radius: 4px;
    position: relative;

    &:checked::after {
        color: #5e913b;
        content: "✔";
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        font-size: 1rem;
    }
`;

const Join = () => {
    const navigate = useNavigate();

    const [termsUseState, setTermsUseState] = useState(false);
    const [termsCheck, setTermsCheck] = useState({
        checked0: false,
        checked1: false,
    });
    const [userData, setUserData] = useState({
        isTermOfUseCheck: false,
        isPrivacyCheck: false,
        username: "",
        password: "",
        nickname: "",
        email: "",
        number: "",
    });
    const inputList = ["아이디", "비밀번호", "닉네임", "이메일", "전화번호"];

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!privacyCheck(userData.password)) {
            return;
        }

        joinPost(userData)
            .then((response) => {
                console.log("joinPost response:", response);
            })
            .catch((error) => {
                console.error("joinPost error:", error);
            });
    };

    const nextButton = (next) => {
        if (next) {
            setTermsUseState(true);
        } else {
            alert("필수 약관에 동의해야 합니다.");
        }
    };

    const privacyCheck = (value) => {
        const regex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
        console.log("^^regex.test(value)", regex.test(value));
        return regex.test(value);
    };

    const nickUniqueCheck = (name) => {
        nickUniquePost(name)
            .then((res) => {})
            .catch((error) => {
                console.error("videosGet error:", error);
            });
    };

    const termsUseView = () => {
        return (
            <div className="flex flex-col">
                <div className="color-green-5e py-[20px] border-b font-semi-bold font-28">
                    인디팬더 회원가입
                </div>
                <div className="py-[20px]">
                    <SubText className="font-medium">이용약관</SubText>
                    <ScrollableBox>{constant.terms_use_text[0].contents}</ScrollableBox>
                    <FlexBox className="font-14 pointer">
                        <StyledCheckbox
                            id="terms_use_text_0"
                            type="checkbox"
                            checked={termsCheck.checked0}
                            onChange={(e) =>
                                setTermsCheck((prev) => ({
                                    ...prev,
                                    checked0: e.target.checked,
                                }))
                            }
                            className="mr-2 pointer"
                        />
                        <label htmlFor="terms_use_text_0" className="text-[#808080] pointer">
                            [필수] 이용약관에 동의합니다.
                        </label>
                    </FlexBox>
                </div>
                <div className="py-[20px]">
                    <SubText className="font-medium">개인정보 수집 및 이용 동의서</SubText>
                    <ScrollableBox>{constant.terms_use_text[1].contents}</ScrollableBox>
                    <FlexBox className="font-14 pointer">
                        <StyledCheckbox
                            id="terms_use_text_1"
                            type="checkbox"
                            checked={termsCheck.checked1}
                            onChange={(e) =>
                                setTermsCheck((prev) => ({
                                    ...prev,
                                    checked1: e.target.checked,
                                }))
                            }
                            className="mr-2 pointer"
                        />
                        <label htmlFor="terms_use_text_1" className="text-[#808080] pointer">
                            [필수] 개인정보 수집 및 이용에 동의합니다.
                        </label>
                    </FlexBox>
                </div>
                <FlexBox justify="center" className="py-[20px] font-16">
                    <button
                        className="mr-4 border-2 px-[12px] py-[8px] rounded-lg text-[#808080]"
                        onClick={() => {
                            navigate(-1);
                        }}
                    >
                        취소
                    </button>
                    <button
                        className="bg-[#5e913b] border-2 border-[#5e913b] px-[12px] py-[8px] rounded-lg text-white"
                        onClick={() => {
                            nextButton(termsCheck.checked0 && termsCheck.checked1);
                        }}
                    >
                        다음
                    </button>
                </FlexBox>
            </div>
        );
    };

    return (
        <BodyContainer>
            {!termsUseState ? (
                termsUseView()
            ) : (
                <>
                    <div className="flex flex-col">
                        <div className="color-green-5e py-[20px] border-b font-semi-bold font-28">
                            개인정보입력
                        </div>
                        <div className="py-[20px]">
                            <form onSubmit={handleSubmit} className="flex flex-col items-center">
                                {Object.keys(userData).map((key, index) => {
                                    if (key === "isTermOfUseCheck" || key === "isPrivacyCheck") {
                                        return inputList;
                                    }
                                    return (
                                        <FlexBox className="h-[30px] mb-[12px] font-16">
                                            <SubText>{inputList[index]}</SubText>
                                            <input
                                                className="w-[200px] h-[30px] p-[5px]"
                                                key={index}
                                                type={key === "password" ? "password" : "text"}
                                                placeholder={
                                                    key.charAt(0).toUpperCase() + key.slice(1)
                                                }
                                                value={userData[key]}
                                                onChange={(e) =>
                                                    setUserData((prev) => ({
                                                        ...prev,
                                                        [key]: e.target.value,
                                                    }))
                                                }
                                            />
                                        </FlexBox>
                                    );
                                })}
                                <FlexBox justify="center" className="py-[20px] font-16">
                                    <button
                                        className="mr-4 border-2 px-[12px] py-[8px] rounded-lg text-[#808080]"
                                        onClick={() => {
                                            setTermsUseState(false);
                                        }}
                                    >
                                        취소
                                    </button>
                                    <button
                                        type="submit"
                                        className="bg-[#5e913b] border-2 border-[#5e913b] px-[12px] py-[8px] rounded-lg text-white"
                                        onClick={() => {
                                            nextButton(termsCheck.checked0 && termsCheck.checked1);
                                        }}
                                    >
                                        다음
                                    </button>
                                </FlexBox>
                            </form>
                        </div>
                    </div>
                </>
            )}
        </BodyContainer>
    );
};

export default Join;
